const express = require('express');
const mysql = require('mysql2');
const cors = require('cors');
const app = express();
const port = 3006;

app.use(cors());

// Database connection
const db = mysql.createConnection({
    host: 'localhost',
    user: 'root',
    password: 'KWurh65ZSp',
    database: 'kati'
});

db.connect((err) => {
    if (err) {
        console.error('Error connecting to the database:', err);
        return;
    }
    console.log('Connected to the database');
});

// Endpoint to fetch price data for a specific country
app.get('/user/:countryCode', (req, res) => {
    const countryCode = req.params.countryCode.toUpperCase();
    const query = `
        SELECT Year_buying, Price_2_rooms, Price_3_rooms, Price_house
        FROM price
        WHERE ID_country = ?
        ORDER BY Year_buying ASC
    `;

    db.query(query, [countryCode], (err, results) => {
        if (err) {
            console.error('Error executing query:', err);
            res.status(500).send('Error fetching data from the database');
            return;
        }

        if (results.length === 0) {
            res.status(404).send('No data found for this country');
            return;
        }

        res.json(results);
    });
});

// Endpoint to fetch neighboring countries for a specific country
app.get('/neigh/:countryCode', (req, res) => {
    const countryCode = req.params.countryCode.toUpperCase();
    const query = `
        SELECT ID_country, ID_neighbor_country
        FROM susedni_durjawi
        WHERE ID_country = ?
        ORDER BY ID_country
    `;

    db.query(query, [countryCode], (err, results) => {
        if (err) {
            console.error('Error executing query:', err);
            res.status(500).send('Error fetching data from the database');
            return;
        }

        if (results.length === 0) {
            res.status(404).send('No data found for this country');
            return;
        }

        res.json(results);
    });
});

// Start the server
app.listen(port, () => {
    console.log(`Server is running on http://localhost:${port}`);
});